import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';
import { HomeComponent } from './components/home/home.component';
import { ArchiveComponent } from './components/archive/archive.component';
import { PopularComponent } from './components/flights/popular/popular.component';
import { AppFeaturesComponent } from './components/home/app-features/app-features.component'; // Import the new component

const routes: Routes = [
  { path: '', component: HomeComponent }, // Default route for Home
  { path: 'popular', component: PopularComponent }, // Popular component
  { path: 'features', component: AppFeaturesComponent }, // App Features component
  { path: ':language?/proyectos', component: ArchiveComponent }, // Archive component with optional language parameter

  { path: 'profile', component: PopularComponent }, // Profile component (placeholder)

  // Wildcard route: Redirects to home if no matching route is found
  { path: '**', pathMatch: 'full', redirectTo: '/' },
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule],
})
export class AppRoutingModule {}
