import { Component, OnInit, AfterViewInit } from '@angular/core';

import {trigger, state, style, animate, transition, stagger, query } from "@angular/animations"
import { AnalyticsService } from 'src/app/services/analytics/analytics.service';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.scss'],
  animations: [
    trigger('bannerTrigger', [
      transition(":enter", [
        query("*", [
          style({ opacity: 0, transform: "translateX(-50px)" }),
          stagger(50, [
            animate(
              "250ms cubic-bezier(0.35, 0, 0.25, 1)",
              style({ opacity: 1, transform: "none" })
            )
          ])
        ])
      ])
    ])
  ]
})
export class BannerComponent implements OnInit {



  constructor(
    public analyticsService: AnalyticsService
  ) { }

  ngOnInit(): void { 
  }


}
<section class="section banner">
  <div class="container banner-wrapper">
    <!-- Left Section: Text -->
    <div class="text-container">
      <h1 class="banner-title">{{ "Banner.Pretitle" | translate }}</h1>
      <p class="banner-subtitle">{{ "Banner.Description" | translate }}</p>
      <div class="cta-buttons">
        <button class="play-store-btn">Get it on Google Play</button>
        <button class="app-store-btn">Available on the Apple Store</button>
      </div>
    </div>

    <!-- Right Section: iPhone Placeholder -->
    <div class="iphone-container">
      <div class="iphone-placeholder">
        <!-- Placeholder for iPhone 15 Pro -->
        <div class="iphone-box">
          <!-- Placeholder iPhone styling -->
        </div>
      </div>
    </div>
  </div>
</section>
