import { Component, OnInit } from '@angular/core';
import { AnalyticsService } from 'src/app/services/analytics/analytics.service';
import { LanguageService } from 'src/app/services/language/language.service';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent implements OnInit {
  cvName: string = ""

  constructor(
    public analyticsService: AnalyticsService,
    public languageService: LanguageService
  ) { }

  ngOnInit(): void {
  }
  downloadCV(){
    this.languageService.translateService.get("Header.cvName").subscribe(val => {
      this.cvName = val
      console.log("val Hello guy",val)
      // app url
      let url = window.location.href;

      // Open a new window with the CV
       window.open(url + "/../assets/cv/" + this.cvName, "_blank");
    })

   }

}

<section class="section journey" id="journey">
  <div class="container">
    <div class="section-box">
      <!-- Placeholder Road -->
      <div class="road"></div>

      <!-- Starting point: Friends are hungry -->
      <div class="journey-point point-a">
        <div class="friend-placeholder hungry">Hungry Friends</div>
        <p>Friends are hungry!</p>
      </div>

      <!-- Animated Car -->
      <div class="journey-car">
        <img src="assets/images/car.png" alt="Car" class="car-img" />
      </div>

      <!-- Middle point: Friends using the app -->
      <div class="journey-point point-b">
        <div class="friend-placeholder app-using">Using Munch Mates</div>
        <p>Friends are on Munch Mates!</p>
      </div>

      <!-- Ending point: Friends are full -->
      <div class="journey-point point-c">
        <div class="friend-placeholder full">Friends are happy and full!</div>
        <p>Friends are full!</p>
      </div>
    </div>
  </div>
</section>
