import { Component, OnInit } from '@angular/core';
import { AnalyticsService } from 'src/app/services/analytics/analytics.service';
import { LanguageService } from 'src/app/services/language/language.service';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent implements OnInit {
  cvName: string = ""

  constructor(
    public analyticsService: AnalyticsService,
    public languageService: LanguageService
  ) { }

  ngOnInit(): void {
  }
  downloadCV(){
    this.languageService.translateService.get("Header.cvName").subscribe(val => {
      this.cvName = val
      console.log("val Hello guy",val)
      // app url
      let url = window.location.href;

      // Open a new window with the CV
       window.open(url + "/../assets/cv/" + this.cvName, "_blank");
    })

   }

}

<section class="section about" id="about">
  <div class="container">
    <div class="section-box">
      <div class="about-title" data-aos="fade-up">
        <h3 class="section-title">
          <span class="n-section-title">01.</span>
          {{ "AboutMe.Title" | translate }}
        </h3>
      </div>
      <div class="row">
        <div class="col-12 col-md-12 mb-4 mb-md-0">
          <div class="about-description">
            <p
              *ngFor="let text of 'AboutMe.Paragraphs' | translate"
              [innerHTML]="text"
              data-aos="fade-up"
              data-aos-duration="1000"
            ></p>
          </div>
        </div>
      </div>

      <!-- iPhone 15 Placeholders -->
      <div class="row iphone-placeholder-wrapper">
        <div class="col-12 col-md-4 text-center">
          <div class="iphone-placeholder">
            <div class="iphone-box"></div>
          </div>
        </div>
        <div class="col-12 col-md-4 text-center">
          <div class="iphone-placeholder">
            <div class="iphone-box"></div>
          </div>
        </div>
        <div class="col-12 col-md-4 text-center">
          <div class="iphone-placeholder">
            <div class="iphone-box"></div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
